<?php $feat_image_slider = wp_get_attachment_url( get_post_thumbnail_id() ); ?>  <div class="slick-image-slide" style="max-height:<?php echo $sliderheightv; ?>px;">				<div class="slide-wrap" style="height:<?php echo $sliderheightv; ?>px;">				<div class="medium-6 wpcolumns left-image-side">					<div class="slide-image-bg" style="height:<?php echo $sliderheightv; ?>px;">					<img src="<?php echo $feat_image_slider; ?>" alt="<?php the_title(); ?>" />					</div>					</div>					<div class="medium-6 wpcolumns">			<div class="slider-content" >			<h2 class="slide-title"><?php the_title(); ?></h2>			<?php if($showContent == "true" ) { ?>				<div class="slider-short-content">					<?php the_content(); ?>							</div>			<?php } 						$sliderurl = get_post_meta( get_the_ID(),'wpsisac_slide_link', true );				if($sliderurl != '') { ?>			<div class="readmore"><a href="<?php echo $sliderurl; ?>" class="slider-readmore"><?php esc_html_e( 'Read More', 'wp-slick-slider-and-image-carousel' ); ?></a></div>				<?php } ?>			</div>			</div>			</div>				</div>